import React, { useEffect, useState } from 'react';
import Clock from '../components/Clock';

const ActionHistory = ({ relay, setRelay, currentPage1, setCurrentPage1 }) => {
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');

    useEffect(() => {
        fetch('http://localhost:8688/api/relay')
            .then((response) => response.json())
            .then((data) => {
                setRelay(data);
            })
            .catch((err) => console.log(err));
    }, []);

    console.log(relay);

    const recordsPerPage = 15;
    const lastIndex = currentPage1 * recordsPerPage;
    const firstIndex = lastIndex - recordsPerPage;

    const filteredRelay = relay.filter((sensor) => {
        const sensorDate = new Date(sensor.date);
        const start = startDate ? new Date(startDate) : new Date('1970-01-01');
        const end = endDate ? new Date(endDate) : new Date();
        return sensorDate >= start && sensorDate <= end;
    });

    const records = filteredRelay.slice(firstIndex, lastIndex);
    const npage = Math.ceil(filteredRelay.length / recordsPerPage);
    const numbers = [...Array(npage + 1).keys()].slice(1);

    const formatISO8601ToDateTime = (isoString) => {
        const date = new Date(isoString);
        const year = date.getFullYear();
        const month = String(date.getMonth() + 1).padStart(2, '0');
        const day = String(date.getDate()).padStart(2, '0');
        const hours = String(date.getHours()).padStart(2, '0');
        const minutes = String(date.getMinutes()).padStart(2, '0');
        const seconds = String(date.getSeconds()).padStart(2, '0');

        return `${day}/${month}/${year} ${hours}:${minutes}:${seconds}`;
    };

    return (
        <div>
            <strong className="h-[90px] border-b mr-[100px] flex justify-start items-center">
                <h1>ACTION HISTORY</h1>
                <Clock />
            </strong>
            <hr className="mr-[100px]" />

            <div className="mt-[20px] mr-[100px]">
                <div className="row mb-3">
                    <div className="col">
                        <label htmlFor="startDate">Start Date: </label>
                        <input
                            type="date"
                            id="startDate"
                            value={startDate}
                            onChange={(e) => setStartDate(e.target.value)}
                        />
                    </div>
                    <div className="col">
                        <label htmlFor="endDate">End Date: </label>
                        <input
                            type="date"
                            id="endDate"
                            value={endDate}
                            onChange={(e) => setEndDate(e.target.value)}
                        />
                    </div>
                </div>

                <div className="row">
                    <table className="table table-striped table-bordered">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>TYPE</th>
                                <th>State</th>
                                <th>Time</th>
                            </tr>
                        </thead>

                        <tbody>
                            {records.map((sensor) => (
                                <tr key={sensor.id}>
                                    <td>{sensor.id}</td>
                                    <td>{sensor.relay_id}</td>
                                    <td>{sensor.state}</td>
                                    <td>
                                        {formatISO8601ToDateTime(sensor.date)}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

                <nav className="flex justify-end mr-[-20px]">
                    <ul className="pagination">
                        {numbers.map((n, i) => {
                            const pagesToShow = 5;
                            const pagesBeforeCurrent = Math.floor(
                                pagesToShow / 2,
                            );
                            const pagesAfterCurrent =
                                pagesToShow - pagesBeforeCurrent;

                            if (n === 1) {
                                return (
                                    <li
                                        className={`page-item ${
                                            currentPage1 === n ? 'active' : ''
                                        }`}
                                        key={i}
                                    >
                                        <a
                                            href="#"
                                            className="page-link"
                                            onClick={() => changeCPage(n)}
                                        >
                                            {n}
                                        </a>
                                    </li>
                                );
                            }

                            if (n === npage) {
                                return (
                                    <li
                                        className={`page-item ${
                                            currentPage1 === n ? 'active' : ''
                                        }`}
                                        key={i}
                                    >
                                        <a
                                            href="#"
                                            className="page-link"
                                            onClick={() => changeCPage(n)}
                                        >
                                            {n}
                                        </a>
                                    </li>
                                );
                            }

                            if (
                                n >= currentPage1 - pagesBeforeCurrent &&
                                n <= currentPage1 + pagesAfterCurrent &&
                                n !== 1 &&
                                n !== npage
                            ) {
                                return (
                                    <li
                                        className={`page-item ${
                                            currentPage1 === n ? 'active' : ''
                                        }`}
                                        key={i}
                                    >
                                        <a
                                            href="#"
                                            className="page-link"
                                            onClick={() => changeCPage(n)}
                                        >
                                            {n}
                                        </a>
                                    </li>
                                );
                            }

                            if (
                                (n === 2 &&
                                    currentPage1 > pagesBeforeCurrent + 1) ||
                                (n === npage - 1 &&
                                    currentPage1 < npage - pagesAfterCurrent)
                            ) {
                                return (
                                    <li className="page-item" key={i}>
                                        <span className="page-link">...</span>
                                    </li>
                                );
                            }

                            return null;
                        })}
                    </ul>
                </nav>
            </div>
        </div>
    );
    function changeCPage(id) {
        setCurrentPage1(id);
    }
};

export default ActionHistory;
